// 获取一个可以用的树堆节点（优先从缓存获取）
func get() *Node {
	if medi == nil {
		return new(Node)
	}
	p := medi
	medi = p.B
	p.B = nil
	return p
}

// 缓存一个树堆节点方便复用
func put(p *Node) {
	p.B = medi
	p.L = nil
	p.R = nil
	medi = p
}

// 维护树堆（保持T字段升序，维护D字段最小堆）
func (this *Heap) maintain(p, q *Node) {
	for q != nil {
		if q.D <= p.D {
			if q.T > p.T { // 这里维护的条件和插入的条件必须完全一致！
				q.L = p
			} else {
				q.R = p
			}
			p.B = q
			break
		}
		p.B, q.B = q.B, p
		if q.T > p.T { // 这里维护的条件和插入的条件必须完全一致！
			q.L, p.R = p.R, q
			if q.L != nil {
				q.L.B = q
			}
		} else {
			q.R, p.L = p.L, q
			if q.R != nil {
				q.R.B = q
			}
		}
		q = p.B
	}
	if q == nil {
		this.Node = p
	}
}

// 完成，即树堆中所有距离信息都被利用完毕
func (this *Heap) Empty() bool {
	return this.Node == nil
}

// 插入距离信息
func (this *Heap) Push(s Distance) {
	var p, q *Node
	// 寻找插入位置
	for p = this.Node; p != nil; {
		if q = p; p.T > s.T { // 这里维护的条件和插入的条件必须完全一致！
			p = p.L
		} else {
			p = p.R
		}
	}
	// 插入节点
	p = get()
	p.Distance, p.B = s, q
	if q != nil {
		if q.T > s.T { // 这里维护的条件和插入的条件必须完全一致！
			q.L = p
		} else {
			q.R = p
		}
	}
	this.maintain(p, q)
}

// 插入距离信息，对目的节点一致的距离信息只保留最短的
func (this *Heap) Update(s Distance) {
	var p, q *Node
	// 寻找插入位置
	for p = this.Node; p != nil; {
		if p.T == s.T {
			break
		}
		if q = p; p.T > s.T { // 这里维护的条件和插入的条件必须完全一致！
			p = p.L
		} else {
			p = p.R
		}
	}
	if p != nil { // 更新节点
		if p.D <= s.D { // 到该节点有更短边，废弃该边
			return
		}
		p.F, p.D = s.F, s.D
	} else { // 插入节点
		p = get()
		p.Distance, p.B = s, q
		if q != nil {
			if q.T > s.T { // 这里维护的条件和插入的条件必须完全一致！
				q.L = p
			} else {
				q.R = p
			}
		}
	}
	this.maintain(p, q)
}

// 提取当前图节点集与外部图节点的最短距离信息
func (this *Heap) Pop() Distance {
	var (
		p, l, r *Node
		q       **Node
		d       Distance
	)
	q = &this.Node
	p = this.Node
	if p != nil { // 旋转至成为叶节点后取出
		for l, r = p.L, p.R; l != nil || r != nil; {
			if l != nil && (r == nil || l.D < r.D) {
				p.L, l.R = l.R, p
				if p.L != nil {
					p.L.B = p
				}
				l.B, p.B = p.B, l
				*q, q = l, &l.R
				l = p.L
			} else {
				p.R, r.L = r.L, p
				if p.R != nil {
					p.R.B = p
				}
				r.B, p.B = p.B, r
				*q, q = r, &r.L
				r = p.R
			}
		}
		*q, d = nil, p.Distance
		put(p)
	}
	return d
}

// 将结构中所有树堆节点均取消指针释放，清空图节点集的字典记录
func (this *Heap) Clean() {
	var p, q *Node
	p = this.Node
	for p != nil {
		if p.L != nil {
			q, p.L = p.L, nil
			q.B, p.B = p.B, q
		}
		if p.R != nil {
			q, p.R = p.R, nil
			q.B, p.B = p.B, q
		}
		q, p = p, p.B
		q.B = nil
	}
}

// 用于实现节点集合（特性：元素总数有限而可预期，方便合并）
// 数组中下标N的元素，其值M如果小于N，则表示M、N属于同一集合
// 同样的，下标M的元素的值也可以指向更小的元素，直至某个元素
// 下标与值相等因此合并两个集合时，只需追溯两条集合链条至头部
// 最小元素，然后将其中下标较大的元素的值设定为较小的那个下标，
// 便可方便实现集合的合并
type Set []int

//创建一个节点集的总集，因为每个元素都等于下标，即都是单元素集合/链条
func NewSet(n int) Set {
	this := make([]int, n)
	for n--; n >= 0; n-- {
		this[n] = n
	}
	return this
}

// 追溯某个集合，直至头部，头部的下标即表示集合的标识符
func (this Set) Trace(n int) int {
	p, q := n, this[n]
	for ; q < n; q = this[n] {
		n = q
	}
	this[p] = n // 此操作是为了在扁平化追溯链条，在节点数量较大时节省查询时间
	return n
}

// 合并两个集合，注意需保证i, j均为Trace返回的头部元素（下标）
func (this Set) Merge(i, j int) {
	if i < j {
		this[j] = i
	} else {
		this[i] = j
	}
}

// Prim算法
func Prim() {
	var (
		have [N]byte // 用来记录节点的集合
		tree Heap
		i, j int
	)
	for {
		have[i] = 1
		for j = 0; j < N; j++ { // 无向图可只需考虑一半，否则需要全部考虑
			if have[j] == 0 {
				tree.Update(Distance{
					i,
					j,
					1 - MTX[i][j],
				})
			}
		}
		if tree.Empty() {
			break
		}
		d := tree.Pop()
		i = d.T
		fmt.Printf("%-2d -> %2d: %.4f\n", d.F, d.T, d.D)
	}
	tree.Clean()
}

// Kruskal算法
func Kruskal() {
	tree := Heap{}
	set := NewSet(N)
	for i := 0; i < N; i++ {
		for j := 0; j < N; j++ { // 无向图可只需考虑一半，否则需要全部考虑
			if i == j {
				continue
			}
			tree.Push(Distance{
				i,
				j,
				1 - MTX[i][j],
			})
		}
	}
	for !tree.Empty() {
		d := tree.Pop()
		f := set.Trace(d.F)
		t := set.Trace(d.T)
		if f != t {
			fmt.Printf("%-2d => %2d: %.4f\n", d.F, d.T, d.D)
			set.Merge(f, t)
		}
	}
	tree.Clean()
}

// Kruskal和Prim的“杂交”算法
func Hybrid() {
	tree := [N]Heap{}
	set := NewSet(N) // 	节点集合
	// 每个图节点创建一个边集合
	for i := 0; i < N; i++ {
		for j := 0; j < N; j++ { // 无向图可只需考虑一半，否则需要全部考虑
			if i == j {
				continue
			}
			tree[i].Push(Distance{
				i,
				j,
				1 - MTX[i][j],
			})
		}
	}
	for i := N - 1; i > 0; i-- {
		I := tree[i]
		d := I.Pop()
		j := set.Trace(d.T)
		J := tree[j]
		set.Merge(i, j)
		fmt.Printf("%-2d >> %2d: %.4f\n", d.F, d.T, d.D)
		p := Heap{}
		for !I.Empty() {
			d := I.Pop()
			if set.Trace(d.T) != j {
				p.Push(d)
			}
		}
		for !J.Empty() {
			d := J.Pop()
			if set.Trace(d.T) != j {
				p.Push(d)
			}
		}
		tree[i] = Heap{} // 清除悬空指针
		tree[j] = p
	}
}

func main() {
	Prim()
	fmt.Println()
	Kruskal()
	fmt.Println()
	Hybrid()
}
